<?php    	/*	Swapping keys and values		array array_flip ( array input)			*/		    $capital['narayani'] = 'chitwan';    $capital['kaski'] = 'pokhara';    $capital['bagmati'] = 'kathmandu';    	echo "<pre>";	print_r($capital);	echo "</pre>";		$flipped = array_flip($capital); //exchanged key and values now		echo "<pre>";	print_r($flipped);	echo "</pre>";	    ?>