<?php /*Grabbing array keys and valuesarray array_keys ( array input [, mixed search_value [, bool strict]])array array_values ( array input)*/	$users[420] = 'ram';    $users[1227] = 'shyam';    $users[1111] = 'hari';    	echo "<pre>";	print_r($users);		echo "<br/>";		$userids = array_keys($users); // grabs all array keys  and returns array of keys    print_r($userids);		echo "<br/>";		$userids = array_keys($users, "ram");    print_r($userids);			$array[] = "Hello";    $array[] = "World";    $array[] = "Foo";    $array[] = "Bar";    $array[] = "Baz";    	echo "<br/>";	print_r($array);	    asort($array);	print_r($array);	echo "<br/>";	    print_r(array_values($array)); 		?>