<?php 	//defining array with array() function	$myarray = array("Kathmandu", "Lalitpur", "Bhaktapur");    $size = count($myarray);		//display array with print_r	echo "<pre>";    print_r($myarray);	echo "</pre>";		//display array with var_dump - shows extra information	echo "<pre>";    var_dump($myarray);	echo "</pre>";		//display array with var_export - output can be used in PHP directly to define array again	echo "<pre>";    var_export($myarray);	echo "</pre>";		//associative arrays	$myarray = array("a"=>"Kathmandu", "b"=>"Lalitpur", "c"=>"Bhaktapur");	echo "<pre>";    print_r($myarray);	echo "</pre>";