<?php/*Creating an array of numbers/charactersarray range ( int low, int high [, int step])*/$questions = range(1,40);$questions = shuffle($questions);echo "<pre>";print_r($questions); echo "<br/>"; $questions = range(1, 10, 2);// gives 1, 3, 5, 7, 9print_r($questions);echo "<br/>";$questions = range(1, 10, 3);// gives 1, 4, 7, 10print_r($questions);echo "<br/>";$questions = range(10, 100, 10); print_r($questions);echo "<br/>";$questions = range(100, 0, 10); //count down 100 90..print_r($questions);echo "<br/>";$questions = range("a", "z", 1); // gives a, b, c, d, ..., x, y, zprint_r($questions);echo "<br/>";$questions = range("z", "a", 2);print_r($questions);echo "<br/>"; ?>