<?php 	$natural_born_killers = array("lions", "tigers", "bears", "kittens");    	echo "<pre>";	print_r($natural_born_killers);		shuffle($natural_born_killers); // can reorder keys - not desirable		print_r($natural_born_killers);		   	//array_rand takes an array to read from, then returns one random key from inside there	print_r(array_rand($natural_born_killers));	echo "<br/>";		print_r(array_rand($natural_born_killers, 2)); //takes two random keys now		?>