<?php /*bool asort ( array input [, int sort_flags]) - sorts an array by its valuesbool ksort ( array input [, int sort_flags]) - sorts an array by its keys.bool arsort ( array input [, int sort_flags]) - reverse sort the array by valuebool krsort ( array input [, int sort_flags]) - reverse sort the array by key*/	$capital['narayani'] = 'chitwan';    $capital['kaski'] = 'pokhara';    $capital['bagmati'] = 'kathmandu';		echo "<pre>";		print_r($capital);		asort($capital);	//returns true on success, sorts by value	print_r($capital); 		ksort($capital); //returns true on succes, sorts by key	print_r($capital);		arsort($capital); //returns true on succes, reverse sorts by value	print_r($capital);		krsort($capital); //returns true on succes, reverse sorts by key	print_r($capital);		?>