<?php if(isset($_GET['page'])){	$content = $_GET['page']; 	$content .= ".php";} else {	$content = "main_content.php";}	?><html><head><title>PHP : Template Idea</title><style>body{text-align:center;}h1{color:#900;margin:10px;}h2{color:#333;}#wrapper {width:700px;margin:0 auto;}#header {height:40px;border:1px solid #bbb;margin-bottom: 10px;line-height:40px;}#content_wrapper {width:auto;}#left_sidebar {width:200px;height:300px;float:left;border:1px solid #bbb;padding:10px;}#content{width:440px;height:300px;float:right;border:1px solid #bbb;padding:10px;}.clear {float:none;clear:both;}#footer {margin-top:10px;height:20px;border:1px solid #bbb;font-size:10px;}</div></style></head><body><div id="wrapper">		<h3>Semicolon Developers of Kathmandu</h3>		<div id="header">			<a href="template.php">Home</a>			<a href="template.php?page=about">About</a>			<a href="template.php?page=services">Services</a>						<a href="template.php?page=contact">Contact Us</a>		</div>		<div id="content_wrapper">			<div id="left_sidebar">			  sidebar content here.			  this content remains static most of the time			</div>						<div id="content">				<!-- this is the are for main content. So let's include our contents here-->								<?php include_once $content; ?> 			</div>					</div>		<div class="clear"></div>				<div id ="footer">			&copy; <?php echo date('Y');?>		</div></div></body></html>